import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "../styles/CartPage.css";

const CartPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const product = location.state?.product; // Get selected product from navigation state

  const handleBack = () => {
    navigate("/products"); // Go back to Product Catalogue
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Cart</h2>
      {product ? (
        <div className="card mx-auto text-center" style={{ width: "300px" }}>
          <img src={product.image} className="card-img-top" alt={product.name} />
          <div className="card-body">
            <h5 className="card-title">{product.name}</h5>
            <p className="card-text">{product.price}</p>
            <button className="btn btn-primary" onClick={handleBack}>Back to Products</button>
          </div>
        </div>
      ) : (
        <p className="text-center">No products in cart.</p>
      )}
    </div>
  );
};

export default CartPage;
